[concat('var axios = require("axios");
var qs = require("qs");
var uuid = require("uuid");
var fs = require("fs")

const { exec } = require("child_process");
const { DefaultAzureCredential } = require("@azure/identity");
const { SecretClient } = require("@azure/keyvault-secrets");

var data_source_names = "";
var glossary_term_guids = "";

function azuread_auth(tenant_id, client_id, client_secret, resource_url) {
    var data = qs.stringify({
        "grant_type": "client_credentials",
        "client_id": `${client_id}`,
        "client_secret": `${client_secret}`,
        "resource": `${resource_url}` 
    });

    var config = {
    headers: { 
        "Content-Type": "application/x-www-form-urlencoded"
        }
    };

    return axios.post(`https://login.microsoftonline.com/${tenant_id}/oauth2/token`, data, config)
    .then(response => response.data)
    .catch(error => error);
}

function create_data_source(access_token, Endpoint, kind, baseUrl, dataSourceName, api_version="2018-12-01-preview") {
    data_source_names = data_source_names + `${dataSourceName},`

    var data = {
        "kind": `${kind}`,
        "properties": {
            "endpoint": `${baseUrl}`
        }
    };

    var config = {
    headers: { 
        "Content-type": "application/json",
        "Authorization": `Bearer ${access_token}`
        }
    };

    return axios.put(`${Endpoint}/datasources/${dataSourceName}?api-version=${api_version}`, data, config)
    .then(response => response.data)
    .catch(error => error);
}

function create_scan(access_token, Endpoint, kind, dataSourceName, scanName, api_version="2018-12-01-preview") {
    var data = {
        "kind": `${kind}`
    };

    var config = {
    headers: { 
        "Content-type": "application/json",
        "Authorization": `Bearer ${access_token}`
        }
    };

    return axios.put(`${Endpoint}/datasources/${dataSourceName}/scans/${scanName}?api-version=${api_version}`, data, config)
    .then(response => response.data)
    .catch(error => error);
}

function run_scan(access_token, Endpoint, dataSourceName, scanName, runId, scanLevel, api_version="2018-12-01-preview") {
    var data = {};

    var config = {
    headers: { 
        "Content-type": "application/json",
        "Authorization": `Bearer ${access_token}`
        }
    };

    return axios.put(`${Endpoint}/datasources/${dataSourceName}/scans/${scanName}/runs/${runId}?scanLevel=${scanLevel}&api-version=${api_version}`, data, config)
    .then(response => response.data)
    .catch(error => error);
}

function create_term_template(access_token, Endpoint, template_name, template_description, attribute_name, attribute_description) {
    var data = {
        "termTemplateDefs": [
                {
                    "name":`${template_name}`,
                    "description":`${template_description}`,
                    "attributeDefs":[
                        {
                        "name":`${attribute_name}`,
                        "description":`${attribute_description}`,
                        "isOptional":"true",
                        "typeName":"string",
                        "options":
                            {
                            "isDisabled":"false"
                            }
                        }
                    ]
                    }
                ]
            };

    var config = {
    headers: { 
        "Content-type": "application/json",
        "Authorization": `Bearer ${access_token}`
        }
    };

    return axios.post(`${Endpoint}/catalog/api/atlas/v2/types/typedefs`, data, config)
    .then(response => response.data)
    .catch(error => error);
}

function get_glossary_details(access_token, account_endpoint) {
    var config = {
        method: "get",
        url: `${account_endpoint}/catalog/api/atlas/v2/glossary`,
        headers: { 
            "Authorization": `Bearer ${access_token}`
        }
    };

    return axios(config)
        .then(response => response.data)
        .catch(error => error);
}

function create_glossary_term(access_token, account_endpoint, template_name, term_name, glossary_guid, term_status="Draft") {
    var obj = {};
    // obj[template_name] = {};  // Pass a term template name as argument in function call below, or comment this line for System Default term template.

    var data = {
        "name":`${term_name}`,
        "anchor": {
            "glossaryGuid":`${glossary_guid}`
            },
        "status":`${term_status}`,
        "nickName":`${term_name}`,

        "attributes": obj
        };

    var config = {
    headers: { 
        "Content-type": "application/json",
        "Authorization": `Bearer ${access_token}`
        }
    };

    return axios.post(`${account_endpoint}/catalog/api/atlas/v2/glossary/term?includeTermHierarchy=true`, data, config)
    .then(response => response.data)
    .catch(error => error);
}



module.exports = async function (context, req) {
    context.log("JavaScript HTTP trigger function processed a request.");


    // ############################
    // AZURE AD AUTHENTICATION
    // ############################

    // Setting up values to be used
    const keyVaultName = "',parameters('keyVaultName'),'";
    const KVUri = "https://" + keyVaultName + ".vault.azure.net";
    const tenant_id = "',variables('tenantId'),'"
    const client_id = "',parameters('aadAppClientId'),'"
    const resource_url = "https://purview.azure.net/"

    // Setting up client for KeyVault
    const credential = new DefaultAzureCredential();
    const client = new SecretClient(KVUri, credential);

    // Retrieving secret from KeyVault
    const retrievedSecret = await client.getSecret("aadAppClientSecret");
    const client_secret=retrievedSecret.value;

    // Retrieve access_token from AzureAuth function
    const token = await azuread_auth(tenant_id, client_id, client_secret, resource_url);
    const access_token = token.access_token;


    // #######################
    // REGISTER BLOB DATA SOURCE
    // #######################
    // Setting up values to be used
    const scan_endpoint = "https://',parameters('purviewAccountName'),'.scan.purview.azure.com";     // Azure Purview scan endpoint
    const account_endpoint = "https://',parameters('purviewAccountName'),'.purview.azure.com";      // Azure Purview account endpoint

    const scan_level = "Full";                                                  // Possible options: Full, Incremental
    const blob_kind = "AzureStorage";
    const blob_storageBaseUrl = "https://',parameters('storageAccountName'),'.blob.core.windows.net/";           // Azure blob service url
    const blob_data_source_name = "DataSource-Blob";                                      // Data Source name to be created
    const blob_scan_name = "qs-blob-scan";
    const blob_scan_kind = "AzureStorageMsi";
    const blob_scan_run_id = uuid.v1();

    // Creating Blob Data Source
    const response_data_source = await create_data_source(access_token, scan_endpoint, blob_kind, blob_storageBaseUrl, blob_data_source_name);
    console.log(response_data_source)
    // Creating Scan
    const response_blob_scan_creation = await create_scan(access_token, scan_endpoint, blob_scan_kind, blob_data_source_name, blob_scan_name);
    console.log(response_blob_scan_creation)
    // Running scan
    const response_blob_run_scan = await run_scan(access_token, scan_endpoint, blob_data_source_name, blob_scan_name, blob_scan_run_id, scan_level);
    console.log(response_blob_run_scan)


    // ########################
    // REGISTER ADLS2 DATA SOURCE
    // ########################

    // Setting up values to be used
    const adls_kind = "AdlsGen2"
    const adls_source_name = "DataSource-AdlsGen2"                                                // Data Source name to be created
    const adls_blob_storageBaseUrl = "https://',parameters('dlsName'),'.dfs.core.windows.net/"              // Adls2 service url
    const adls_scan_name = "qs-adls-scan";
    const adls_scan_kind = "AdlsGen2Msi";
    const adls_scan_run_id = uuid.v1();

    // Creating AdlsGen2 Data Source
    const response_adls_data_source = await create_data_source(access_token, scan_endpoint, adls_kind, adls_blob_storageBaseUrl, adls_source_name);
    console.log(response_adls_data_source)
    // Creating Scan
    const response_adls_scan_creation = await create_scan(access_token, scan_endpoint, adls_scan_kind, adls_source_name, adls_scan_name);
    console.log(response_adls_scan_creation)
    // Running scan
    const response_adls_run_scan = await run_scan(access_token, scan_endpoint, adls_source_name, adls_scan_name, adls_scan_run_id, scan_level);
    console.log(response_adls_run_scan)

    // #################
    // BUSINESS GLOSSARY
    // #################

    const glossary_details = await get_glossary_details(access_token, account_endpoint)
    context.log(glossary_details)

    // Setting up values to be used
    const glossary_guid = glossary_details[0].guid;
    const template_name = "Purview-QS";
    const term_name = "qs-demo-term";

    // Creating glossary term
    const response_create_term = await create_glossary_term(access_token, account_endpoint, template_name, term_name, glossary_guid);
    console.log(response_create_term)
    glossary_term_guids = glossary_term_guids + `${response_create_term.guid},`         // appending to GUID string


    // #################
    // UPDATING SECRETS
    // #################

    // Adding source names and term guids to Azure keyvault secrets
    const result_sources = await client.setSecret("DataSources", data_source_names);
    console.log(result_sources)
    const result_terms = await client.setSecret("TermGUIDs", glossary_term_guids);
    console.log(result_terms)

    context.res = {
        body: `AzurePurview Creation function triggered successfully..`
    };
}')]

